    @Override
    public ProductEntity updateProductById(Long id, ProductEditViewModel productEditViewModel) {
        ProductEntity product = this.productRepository.findById(id).get();
        return this.updateProduct(product, productEditViewModel);
    }

    private ProductEntity updateProduct(ProductEntity oldVersion, ProductEditViewModel newData) {

        CategoryEntity category = this.categoryService.findByCategory(newData.getCategory());
        LocationEntity location = this.locationService.findByLocation(newData.getLocation());

        oldVersion.
                setTitle(newData.getTitle()).
                setCondition(newData.getCondition()).
                setCategory(category).
                setDescription(newData.getDescription()).
                setPrice(newData.getPrice()).
                setLocation(location).
                setPromo(newData.getIsPromo());


        Optional<PictureEntity> picture =
                this.pictureService.findByUrl(newData.getUrlPicture());

        if (picture.isEmpty()) {
            PictureEntity newPicture = new PictureEntity();
            newPicture.
                    setUrl(newData.getUrlPicture()).
                    setProduct(oldVersion);
            Long oldPictureId = oldVersion.getPicture().getId();
            oldVersion.setPicture(newPicture);
            //todo: here should delete a old picture from db
            this.pictureService.deleteOldPictureById(oldPictureId);

            newPicture = this.pictureService.addPictureInDb(newPicture);
        }

        oldVersion.setModified(LocalDateTime.now());
        return this.productRepository.save(oldVersion);
    }

    @Override
    public ProductDetailsViewDto getAndIncreaseViewsProductById(Long id) {

        ProductEntity currentProduct = this.findById(id);

        if (currentProduct == null) {
            return null;
        }
        currentProduct.setViews(currentProduct.getViews() + 1);
        currentProduct = this.addProductEntity(currentProduct);
        return this.modelMapper.map(currentProduct, ProductDetailsViewDto.class);
    }

    @Override
    public Set<ProductChatViewModel> getProductsFromChatsByUserByUserId(Long id) {

        Set<ProductEntity> products = this.findProductsByUserId(id);

        Set<MessageEntity> receivedMessages = this.userService.findById(id).getReceiverMessages();
        Set<MessageEntity> sendedMessages = this.userService.findById(id).getSendMessages();

        for (MessageEntity receivedMessage : receivedMessages) {
            if (receivedMessage.getProduct().getSeller().getId() != id) {
                products.add(receivedMessage.getProduct());
            }
        }
        for (MessageEntity sendedMessage : sendedMessages) {
            if (sendedMessage.getProduct().getSeller().getId() != id) {
                products.add(sendedMessage.getProduct());
            }
        }
        products = products.stream().filter(m -> m.getMessages().size() > 0).collect(Collectors.toSet());

        return products.stream().map(p -> mapToProductChatViewModel(p)).collect(Collectors.toSet());
    }

    @Override
    public Set<UserChatViewModel> findProductChattersByProductIdAndSellerId(Long productId, Long sellerId) {

        Set<MessageEntity> messages = this.findById(productId).getMessages();

        Set<UserChatViewModel> chatters = new HashSet<>();

        for (MessageEntity message : messages) {
            if ((message.getSender().getId() != sellerId && (message.getReceiver().getId() == sellerId))) {
                chatters.add(this.modelMapper.map(message.getSender(), UserChatViewModel.class));
            }
        }
        return chatters;
    }

    // Returning a random three promotions products and save them in cache.
    @Cacheable("randomProducts")
    @Override
    public List<ProductSearchViewModel> getThreeRandomProducts() {
        List<ProductSearchViewModel> promotionProductsViews = this.
                filterBy(new ProductSearchingBindingModel(), null, true);
        return this.returnThreeRandomProductsFromPromotionProductsViewsList(promotionProductsViews);
    }


    // Returning a random three promotions products
    private List<ProductSearchViewModel> returnThreeRandomProductsFromPromotionProductsViewsList(List<ProductSearchViewModel> promotionProductsViews) {
        List<ProductSearchViewModel> returnedThreePromotion = new LinkedList<>();
        List<Integer> lastSavedIndex = new LinkedList<>();

        for (int i = 0; i < 3; ) {
            Random random = new Random();
            int randomIndex = random.nextInt(promotionProductsViews.size());
            if (!lastSavedIndex.contains(randomIndex)) {
                lastSavedIndex.add(randomIndex);
                returnedThreePromotion.add(promotionProductsViews.get(randomIndex));
                i++;
            }
        }
        return returnedThreePromotion;
    }

    private ProductChatViewModel mapToProductChatViewModel(ProductEntity productEntity) {
        return this.modelMapper.map(productEntity, ProductChatViewModel.class);
    }

